import React, { useState } from "react";

interface CellProp {
  row: number;
  col: number;
  buffer: string;
  isSelected: boolean;
}

export default function EditableCell({
  row,
  col,
  buffer,
  isSelected,
}: CellProp) {
  // create handler function for buffer when enter is pressed for inputbox

  // usecallback function that sends which cell is selected to selectedcellbox

  if (isSelected) {
    return (
      <div className="w-24 h-7 border-2 bg-pink-200 border-solid border-pink-800 text-left">
        {buffer}
      </div>
    );
  } else {
    return (
      <div className="w-24 h-7 border-2 bg-gray-200 border-solid border-gray-500 text-left">
        {buffer}
      </div>
    );
  }
}
