import React, { useState } from "react";
import { SpreadsheetCellModel } from "../model/domain/cells/spreadsheet-cell.model";

interface CellProp {
  cellModel: SpreadsheetCellModel;
  handleOnClick(key: string): void;
}

export default function SpreadsheetCell({
  cellModel,
  handleOnClick,
}: CellProp) {
  const [selected, setSelected] = useState(cellModel.getIsSelected());

  const handleSetSelected = () => {
    cellModel.setIsSelected(!cellModel.getIsSelected());
    setSelected(cellModel.getIsSelected());
    handleOnClick(cellModel.getReference());
  };

  if (selected) {
    return (
      <div
        className="w-24 h-7 border-2 bg-pink-200 border-solid border-pink-800 text-left"
        onClick={handleSetSelected}
      >
        {cellModel.getDisplay()}
      </div>
    );
  } else {
    return (
      <div
        className="w-24 h-7 border-2 bg-gray-200 border-solid border-gray-500 text-left"
        onClick={handleSetSelected}
      >
        {cellModel.getDisplay()}
      </div>
    );
  }
}
