import { ICell } from "../../interfaces/cell.interface";
import { CellText } from "./cell-text.model";
import { create, all } from "mathjs";

export class SpreadsheetCellModel implements ICell {
  private isSelected: boolean;
  private textVisual: CellText;
  private _buffer: string;
  private _display: string;
  private _reference: string;

  public constructor(row: number, col: number) {
    this._buffer = "";
    this._display = "";
    this.isSelected = false;
    this._reference = this.determineReference(row.toString(), col.toString());
    this.textVisual = new CellText.Builder().build();
  }

  public setBuffer(buffer: string, replacedBuffer?: string): void {
    this._buffer = buffer;

    if (replacedBuffer !== undefined) {
      try {
        const math = create(all, {});
        this._display = String(math.evaluate(replacedBuffer));
      } catch {
        throw new Error("Could not compute expression");
      }
    } else {
      this._display = this._buffer;
    }
  }

  public getBuffer(): string {
    return this._buffer;
  }

  public getIsSelected(): boolean {
    return this.isSelected;
  }

  public setIsSelected(isSelected: boolean): void {
    this.isSelected = isSelected;
  }

  public getDisplay(): string {
    return this._display;
  }

  public determineReference(row: string, col: string): string {
    const colChar = col.charAt(0).toUpperCase();
    const rowNum = row;

    return colChar + rowNum;
  }

  public getReference(): string {
    const ref = this._reference;
    return ref;
  }

  public setReference(row: string, col: number) {
    const colChar = col.toString().charAt(0).toUpperCase();
    const rowNum = row;
    this._reference = colChar + rowNum;
  }

  public clearCell(): void {
    this._buffer = "";
    this._display = "";
  }
}
