import React, { useState } from "react";

interface Cell {
  row: number;
  col: number;
  buffer: string;
}

// MAYBE CONVERT TO USEMEMO TO PREVENT RE-RENDERING

// CHANGE SETTER FUNCTION BECAUSE MULTIPLE CELLS CAN BE SELECTED WHEN YOU CLICK ON THEM, SHOULD ONLY BE ONE
export default function EditableCell({ row, col, buffer }: Cell) {
  const [isSelected, setSelected] = useState(false);

  // create handler function for buffer when enter is pressed for inputbox

  // usecallback function that sends which cell is selected to selectedcellbox

  if (isSelected) {
    return (
      <div
        className="w-24 h-7 border-2 bg-pink-200 border-solid border-pink-800 text-left"
        onClick={() => setSelected(false)}
      >
        {buffer}
      </div>
    );
  } else {
    return (
      <div
        className="w-24 h-7 border-2 bg-gray-200 border-solid border-gray-500 text-left"
        onClick={() => setSelected(true)}
      >
        {buffer}
      </div>
    );
  }
}
