import React from "react";
import SpreadsheetCell from "./SpreadsheetCell.component";
import { SpreadSheetPageModel } from "../model/domain/spreadsheet-page.model";
import { SpreadsheetCellModel } from "../model/domain/cells/spreadsheet-cell.model";

interface PageProp {
  pageModel: SpreadSheetPageModel;
  handleCellClick(key: string): void;
  selectedCell: SpreadsheetCellModel;
  setCellValue(input: string): void;
}

export default function SpreadsheetPage({
  pageModel,
  handleCellClick,
  selectedCell,
  setCellValue,
}: PageProp) {
  const sheetCells: SpreadsheetCellModel[][] = pageModel.getSheet();

  const handleCellDisplay = (cell: SpreadsheetCellModel) => {
    cell.setBuffer("Test");
    return cell.getDisplay();
  };

  const renderCells = () => {
    const rows = sheetCells.map((row, rowIndex) => {
      const cols = row.map((cell, colIndex) => {
        return (
          <SpreadsheetCell
            key={pageModel.getCellAt(rowIndex, colIndex).getReference()}
            cellModel={cell}
            isSelected={
              selectedCell.getReference() ===
              pageModel.getCellAt(rowIndex, colIndex).getReference()
            }
            handleCellClick={handleCellClick}
            cellDisplay={handleCellDisplay(
              pageModel.getCellAt(rowIndex, colIndex)
            )}
            updateCellDisplay={setCellValue}
          />
        );
      });

      return (
        <div key={rowIndex} className="flex flex-row">
          {cols}
        </div>
      );
    });
    return rows;
  };

  return <>{renderCells()}</>;
}
