import React, { useState } from "react";
import SpreadsheetCell from "./SpreadsheetCell.component";
import { SpreadSheetPageModel } from "../model/domain/spreadsheet-page.model";
import { SpreadsheetCellModel } from "../model/domain/cells/spreadsheet-cell.model";

interface PageProp {
  pageModel: SpreadSheetPageModel;
}

export default function SpreadsheetPage({ pageModel }: PageProp) {
  const [selectedPageCell, setSelectedPageCell] = useState(
    pageModel.getSelectedCell()
  );

  const sheetCells: SpreadsheetCellModel[][] = pageModel.getSheet();

  const updateSelectedCell = (reference: string) => {
    pageModel.setSelectedCell(pageModel.getCellFromReference(reference));
    setSelectedPageCell(pageModel.getSelectedCell());
  };

  const renderCells = () => {
    const rows = sheetCells.map((row, rowIndex) => {
      const cols = row.map((cell, colIndex) => {
        return (
          <SpreadsheetCell
            cellModel={pageModel.getCellAt(rowIndex, colIndex)}
            handleCellClick={updateSelectedCell}
          />
        );
      });

      return (
        <div key={rowIndex} className="flex flex-row">
          {cols}
        </div>
      );
    });
    return rows;
  };

  return <>{renderCells()}</>;
}
