import React from "react";
import EditableCell from "./EditableCell.component";

interface CellProp {
  row: number;
  col: number;
  buffer: string;
}
interface PageProp {
  rows: number;
  cols: number;
  selectedCell: string;
  setSelectedCell(): void;
}

export default function SpreadsheetPage({
  rows,
  cols,
  selectedCell,
  setSelectedCell,
}: PageProp) {
  const generateCells = (r: number, c: number): CellProp[][] => {
    const cells: CellProp[][] = [];
    for (let i = 0; i < r; i++) {
      const row: CellProp[] = [];
      for (let j = 0; j < c; j++) {
        row.push({ row: i, col: j, buffer: `${i}, ${j}` });
      }
      cells.push(row);
    }
    return cells;
  };

  const renderCells = (cells: CellProp[][]) => {
    const rows = cells.map((row, i) => {
      const cols = row.map((cell, j) => {
        const isSelected = selectedCell === `${(i, j)}`;
        return (
          <EditableCell
            key={j}
            // add property for cell key {col}{row}
            cellValue={cell.buffer}
            isSelected={false}
            handleOnClick={setSelectedCell}
          />
        );
      });
      return (
        <div key={i} className="flex flex-row">
          {cols}
        </div>
      );
    });
    return rows;
  };

  return <>{renderCells(generateCells(rows, cols))}</>;
}
