import { Expressions } from "../../expressions/expression-enums.model";
import { ICell } from "../interfaces/cell.interface";
import { ISpreadsheetPage } from "../interfaces/spreadsheet-page.interface";
import { SpreadsheetCellModel } from "./cells/spreadsheet-cell.model";

export class SpreadSheetPageModel implements ISpreadsheetPage {
  private _sheetName: string;
  private sheet: SpreadsheetCellModel[][];
  private selectedCells: ICell[];

  public constructor(name: string, nArray?: number) {
    this._sheetName = name;
    this.selectedCells = [];
    this.sheet = [];

    if (nArray !== undefined && nArray > 0) {
      for (let r: number = 0; r < nArray; r++) {
        this.sheet[r] = [];
        for (let c: number = 0; c < nArray; c++) {
          this.sheet[r][c] = new SpreadsheetCellModel(r, c);
        }
      }
    } else {
      for (let r: number = 0; r < 15; r++) {
        this.sheet[r] = [];
        for (let c: number = 0; c < 15; c++) {
          this.sheet[r][c] = new SpreadsheetCellModel(r, c);
        }
      }
    }
  }

  public get sheetName(): string {
    return this._sheetName;
  }

  // find workaround
  public getSheet(): SpreadsheetCellModel[][] {
    return this.sheet;
  }

  // find workaround
  public getCellAt(row: number, col: number): SpreadsheetCellModel {
    return this.sheet[row][col];
  }

  public getValueAtCell(cellReference: string): string {
    const cell = this.getCellFromReference(cellReference);
    return cell.getDisplay();
  }

  public setValue(cellReference: string, value: string): void {
    const cell = this.getCellFromReference(cellReference);
    let expression = "";
    if (value.startsWith("=", 0)) {
      try {
        expression = this.evaluateExpression(value.substring(1));
        cell.setBuffer(value, expression);
      } catch (error) {
        throw new Error(`Invalid Expression ${error}`);
      }
    } else {
      cell.setBuffer(value);
    }
  }

  private getCellFromReference(cellReference: string): ICell {
    // NEED TO CHECK THAT INPUT IS VALID
    const colChar = cellReference.charAt(0).toUpperCase();
    const col = colChar.charCodeAt(0) - "A".charCodeAt(0);
    const row = parseInt(cellReference.substring(1)) - 1;

    return this.sheet[row][col];
  }

  public addRow(index: number): void {
    if (index < 0 || index > this.sheet.length) {
      throw new Error("Invalid row index");
    }

    const newRow: SpreadsheetCellModel[] = [];
    for (let col = 0; col < this.sheet[0].length; col++) {
      newRow[col] = new SpreadsheetCellModel(index, col);
    }

    this.sheet.splice(index, 0, newRow);
  }

  public addColumn(index: number): void {
    if (index < 0 || index > this.sheet[0].length) {
      throw new Error("Invalid column index");
    }

    for (let row = 0; row < this.sheet.length; row++) {
      this.sheet[row].splice(index, 0, new SpreadsheetCellModel(row, index));
    }
  }

  public removeRow(index: number): void {
    if (index < 0 || index >= this.sheet.length) {
      throw new Error("Invalid row index");
    }

    this.sheet.splice(index, 1);
  }

  public removeColumn(index: number): void {
    if (index < 0 || index >= this.sheet[0].length) {
      throw new Error("Invalid column index");
    }

    for (let row = 0; row < this.sheet.length; row++) {
      this.sheet[row].splice(index, 1);
    }
  }

  public getNrRows(): number {
    return this.sheet.length;
  }
  public getNrCols(): number {
    return this.sheet[0].length;
  }

  private evaluateExpression(input: string): string {
    try {
      while (input.includes(Expressions.REF)) {
        input = this.evaluateRefExpressions(input);
      }

      while (input.includes(Expressions.SUM)) {
        input = this.evaluateSumExpressions(input);
      }

      while (input.includes(Expressions.AVG)) {
        input = this.evaluateAvgExpressions(input);
      }

      return input;
    } catch (error) {
      throw new Error(`Failed to replace expressions ${error}`);
    }
  }

  private evaluateRefExpressions(input: string): string {
    const refRegex = /REF\(([^)]+)\)/g;
    return input.replace(refRegex, (_, cellReference) =>
      this.getCellFromReference(cellReference).getDisplay()
    );
  }

  private evaluateSumExpressions(input: string): string {
    const sumRegex = /SUM\(([^)]+)\)/g;
    return input.replace(sumRegex, (_, range) => this.sumCells(range));
  }

  private evaluateAvgExpressions(input: string): string {
    const avgRegex = /AVG\(([^)]+)\)/g;
    return input.replace(avgRegex, (_, range) => this.averageCells(range));
  }

  //TODO
  private sumCells(range: string): string {
    let sum: number = 0;
    let cellRange = [];

    if (range.match("^[^:]+:[^:]+$")) {
      // regex to match expressions using colon for cell range
      const [startCell, endCell] = range.split(":");
      cellRange = this.getRangeOfCells(startCell, endCell);
    } else if (range.match("^[A-Za-z]\\d+(,\\s?[A-Za-z]\\d+)*$")) {
      // regex to match expressions using comma
      cellRange = range.split(",");
    } else {
      throw new Error(`Syntax for reference is not correct. ${range}`);
    }

    for (let cell of cellRange) {
      let cellVal = Number(this.getCellFromReference(cell).getDisplay());
      if (Number.isNaN(cellVal)) {
        throw new Error("Cell is not a Number");
      } else {
        sum += cellVal;
      }
    }

    return sum.toString();
  }

  private averageCells(range: string): string {
    let sum: number = 0;
    let cellRange = [];

    if (range.match("^[^:]+:[^:]+$")) {
      // regex to match expressions using colon for cell range
      const [startCell, endCell] = range.split(":");
      cellRange = this.getRangeOfCells(startCell, endCell);
    } else if (range.match("^[A-Za-z]\\d+(,\\s?[A-Za-z]\\d+)*$")) {
      // regex to match expressions using comma
      cellRange = range.split(",");
    } else {
      throw new Error(`Syntax for reference is not correct. ${range}`);
    }

    let count = cellRange.length;

    for (let cell of cellRange) {
      let cellVal = Number(this.getCellFromReference(cell).getDisplay());
      if (Number.isNaN(cellVal)) {
        throw new Error("Cell is not a Number");
      } else {
        sum += cellVal;
      }
    }

    let average = sum / count;
    return average.toString();
  }

  private getRangeOfCells(startCell: string, endCell: string): string[] {
    let cells: string[] = [];
    const [startCol, startRow] = startCell.split("");
    const [endCol, endRow] = endCell.split("");

    const startColIdx = startCol.charCodeAt(0) - "A".charCodeAt(0);
    const endColIdx = endCol.charCodeAt(0) - "A".charCodeAt(0);
    const startRowIdx = Number(startRow);
    const endRowIdx = Number(endRow);

    for (let i = startRowIdx; i <= endRowIdx; i++) {
      for (let j = startColIdx; j <= endColIdx; j++) {
        cells.push(this.getColumnName(j) + i);
      }
    }

    return cells;
  }

  private getColumnName(columnNumber: number): string {
    return String.fromCharCode(columnNumber + 65);
  }
}
