import React from "react";
import { SpreadsheetCellModel } from "../model/domain/cells/spreadsheet-cell.model";

interface CellProp {
  cellModel: SpreadsheetCellModel;
  handleCellClick(key: string): void;
  isSelected: boolean;
  setCellValue(input: string): void;
}

export default function SpreadsheetCell({
  cellModel,
  handleCellClick,
  isSelected,
  setCellValue,
}: CellProp) {
  const handleClick = () => {
    handleCellClick(cellModel.getReference());
  };

  if (isSelected) {
    return (
      <div
        className="w-24 h-7 border-2 bg-pink-200 border-solid border-pink-800 text-left"
        onClick={handleClick}
      >
        {cellModel.getDisplay()}
      </div>
    );
  } else {
    return (
      <div
        className="w-24 h-7 border-2 bg-zinc-300 border-solid border-zinc-700 text-left"
        onClick={handleClick}
      >
        {cellModel.getDisplay()}
      </div>
    );
  }
}
