import React, { useState } from "react";
import "./globals.css";
import Header from "./components/layout/Header";
import SelectedCellBox from "./components/SelectedCellBox.component";
import InputBox from "./components/InputBox.component";
import SpreadSheetPage from "./components/SpreadsheetPage.component";
import { SpreadSheetPageModel } from "./model/domain/spreadsheet-page.model";
import PagesButton from "./components/layout/PagesButton";
import ImportButton from "./components/layout/ImportButton";
import ExportButton from "./components/layout/ExportButton";
import { SpreadsheetCellModel } from "./model/domain/cells/spreadsheet-cell.model";

/*
CHANGES MADE BECAUSE I SCREWED UP THE MR
- changed how cell reference is computed
- added row/col attribute to cell and page
*/

export default function SpreadsheetMain() {
  const pageModel: SpreadSheetPageModel = new SpreadSheetPageModel("", 17);
  const [inputDisplay, setInputDisplay] = useState("");
  const [selectedPageCell, setSelectedPageCell] = useState(
    pageModel.getSelectedCell()
  );

  const updateSelectedCell = (
    cell: SpreadsheetCellModel,
    reference: string
  ) => {
    pageModel.setSelectedCell(cell);
    cell.setStringReference(reference);
    setSelectedPageCell(cell);
  };

  const handleInputEnter = (input: string): void => {
    selectedPageCell.setBuffer(input);
    setInputDisplay("");
    setInputDisplay(
      `Enter pressed. Selected Cell Reference: ${selectedPageCell.getReference()}. Display=${selectedPageCell.getDisplay()}`
    );
  };

  //  *** NEED TO FIX CELL'S DISPLAY UPDATING
  // usestate celldisplay in cell component, setter function in parent and passed to state
  // also maybe useeffect
  // * NEED TO CREATE NEW OBJECT INSTEAD OF DIRECTLY UPDATING OBJECT PROPERTIES
  // create method in cell that returns a new cell object with given display

  return (
    <>
      <div className="main-container">
        <Header></Header>
        <div className="flex justify-center">
          <div className="flex flex-col pt-5">
            <div className="flex flex-row w-full space-x-2">
              <ImportButton />
              <ExportButton />
            </div>
            <div className="flex flex-row w-full pt-2">
              <PagesButton />
            </div>
            <div className="flex flex-row w-full">
              <SelectedCellBox display={selectedPageCell.getReference()} />
              <InputBox
                inputEnter={handleInputEnter}
                displayValue={inputDisplay}
                setInputDisplay={setInputDisplay}
              />
            </div>
            <SpreadSheetPage
              pageModel={pageModel}
              handleCellClick={updateSelectedCell}
              selectedCell={selectedPageCell}
            />
          </div>
        </div>
      </div>
    </>
  );
}
