import React, { useState } from "react";

interface InputProp {
  inputEnter(input: string): void;
}

export default function InputBox({ inputEnter }: InputProp) {
  const [displayValue, setDisplayValue] = useState("");

  return (
    <input
      className="w-4/5 text-zinc-900 border-2 border-zinc-800 bg-zinc-400 hover:bg-zinc-300 focus:bg-zinc-300 transition duration-90 ease-in-out text-xl h-12"
      onKeyDown={(e) => {
        if (e.key === "Enter") {
          inputEnter(displayValue);
          setDisplayValue("");
        }
      }}
      onChange={(e) => setDisplayValue(e.target.value)}
    ></input>
  );
}
