import React, { useEffect } from "react";
import SpreadsheetCell from "./SpreadsheetCell.component";
import { SpreadSheetPageModel } from "../model/domain/spreadsheet-page.model";
import { SpreadsheetCellModel } from "../model/domain/cells/spreadsheet-cell.model";

interface PageProp {
  pageModel: SpreadSheetPageModel;
  handleCellClick(reference: string): void;
  selectedCell: string;
}

export default function SpreadsheetPage({
  pageModel,
  handleCellClick,
  selectedCell,
}: PageProp) {
  function determineReference(row: number, col: number): string {
    const colChar = String.fromCharCode(64 + col);
    const rowNum = row.toString();

    return colChar + rowNum;
  }

  const rowNum = pageModel.getNumRows();
  const colNum = pageModel.getNumCols();

  const renderCells = () => {
    const rows = [];
    for (let i = 0; i < rowNum; i++) {
      const cols = [];
      for (let j = 0; j < colNum; j++) {
        if (i === 0 && j === 0) {
          cols.push(
            <div className="w-24 h-7 border-2 bg-zinc-400 border-solid border-zinc-700 text-left"></div>
          );
        } else if (i === 0) {
          cols.push(
            <div
              key={j}
              className="w-24 h-7 border-2 bg-zinc-400 border-solid border-zinc-700 text-left"
            >
              {String.fromCharCode(64 + j)}
            </div>
          );
        } else if (j === 0) {
          cols.push(
            <div
              key={i}
              className="w-24 h-7 border-2 bg-zinc-400 border-solid border-zinc-700 text-left"
            >
              {i}
            </div>
          );
        } else {
          const cell: SpreadsheetCellModel = pageModel.getCellFromReference(
            determineReference(i, j)
          );
          cols.push(
            <SpreadsheetCell
              key={determineReference(i, j)}
              reference={determineReference(i, j)}
              isSelected={selectedCell === determineReference(i, j)}
              handleCellClick={handleCellClick}
              cellDisplay={cell.getDisplay()}
            />
          );
        }
      }
      rows.push(
        <div key={i} className="flex flex-row">
          {cols}
        </div>
      );
    }
    return rows;
  };

  return <>{renderCells()}</>;
}
