import React, { useState } from "react";
import "./globals.css";
import Header from "./components/layout/Header";
import SelectedCellBox from "./components/SelectedCellBox.component";
import InputBox from "./components/InputBox.component";
import SpreadSheetPage from "./components/SpreadsheetPage.component";
import { SpreadSheetPageModel } from "./model/domain/spreadsheet-page.model";

/*
TODO

- add buttons; pages, import, export
- update setvalue handler function to properly update value based on model
- tidy up code

*/

/*
CHANGES MADE BECAUSE I SCREWED UP THE MR
- changed how cell reference is computed
- added row/col attribute to cell and page
*/

export default function SpreadsheetMain() {
  const pageModel: SpreadSheetPageModel = new SpreadSheetPageModel("", 17);
  // have data model as usestate? in spreadsheetpage component
  // useeffect updates it any time data has changed

  const [inputDisplay, setInputDisplay] = useState("");

  // move to page itself
  const [selectedPageCell, setSelectedPageCell] = useState(
    pageModel.getSelectedCell()
  );

  const updateSelectedCell = (reference: string) => {
    pageModel.setSelectedCell(pageModel.getCellFromReference(reference));
    setSelectedPageCell(pageModel.getSelectedCell());
  };

  const handleInputEnter = (input: string) => {
    updateCellValue(input);
    setInputDisplay(selectedPageCell.getReference());
  };

  //  *** NEED TO FIX CELL'S DISPLAY UPDATING
  // usestate celldisplay in cell component, setter function in parent and passed to state
  // also maybe useeffect
  const updateCellValue = (input: string) => {
    selectedPageCell.setBuffer(input);
  };

  return (
    <>
      <div className="main-container">
        <Header></Header>
        <div className="flex justify-center">
          <div className="flex flex-col pt-5">
            <div className="flex flex-row w-full">
              <button>hello</button>
              <button>hello</button>
            </div>
            <div className="flex flex-row w-full">
              <button>hello</button>
              <button>hello</button>
            </div>
            <div className="flex flex-row w-full">
              <SelectedCellBox display={selectedPageCell.getReference()} />
              <InputBox
                inputEnter={handleInputEnter}
                displayValue={inputDisplay}
                setInputDisplay={setInputDisplay}
              />
            </div>
            <SpreadSheetPage
              pageModel={pageModel}
              handleCellClick={updateSelectedCell}
              selectedCell={selectedPageCell}
              setCellValue={updateCellValue}
            />
          </div>
        </div>
      </div>
    </>
  );
}
